import React from "react";
import Endpoints from "./hooks/Endpoints"

const botaoAbrirPortFolio = <button>Conhecer Lucas...</button>
const botaoListarClientes = <button>Listar clientes</button>
const botaoCadastrarClientes = <button>Cadastrar Clientes</button>

const CONFI_PERMITIR_LISTAR_CLIENTES = false;

const App = () => {
    return (
        <div className="App">
            <header>
                <h1>Bem vindos ao meu PortFolio.</h1>
                <p>Bora descobrir um pouco mais sobre mim?</p>
                {botaoAbrirPortFolio}
                <p>______________________________________________________________________________________________________________________________________</p>
            </header>

            {CONFI_PERMITIR_LISTAR_CLIENTES ? (
                <div>
                    <h2>Lista de Clientes</h2>
                    <p>Para listar os clientes, clique no botão abaixo:</p>
                    {botaoListarClientes}

                </div>
            ) : (
                <div>
                    <h2>Ainda não existem clientes cadastrados. Clique no botão abaixo para cadastrar.</h2>
                    {botaoCadastrarClientes}
                    <Endpoints/>
                </div>
            )}
        </div>
    );
};

export default App;
