import React from "react";
import Endpoints from "./hooks/Endpoints"

const botaoAbrirPortFolio = <button>Conhecer Lucas...</button>
const botaoListarClientes = <button id="btnListarClientesId">Listar clientes</button>
const botaoCadastrarClientes = <button>Cadastrar Clientes</button>

const CONFI_PERMITIR_LISTAR_CLIENTES = true;

const listClientes = [
    {
        id: 1,
        nome: "Lucas da Costa"
    },
    {
        id: 2,
        nome: "Naira Aparecida da Silva Braga"
    },

    {
        id: 3,
        nome: "Moana da Silva Costa"
    },

]

const App = () => {
    const listarClientes = (customer, index) => (
        <header>
        <h1>Bem vindo ao Sistema de Clientes</h1>
        <div>
            <h2>Lista de Clientes</h2>
            <p>Para listar os clientes, clique no botão abaixo:</p>
            {botaoListarClientes}
             <li>{customer.nome}</li>
            <Endpoints />

        </div>
    </header>
    )
    
    const cadastrarClientes = () => (
        <div>
        <h2>Ainda não existem clientes cadastrados. Clique no botão abaixo para cadastrar.</h2>
        {botaoCadastrarClientes}
    </div>
    )
     
    return (
        <div className="App">
            {CONFI_PERMITIR_LISTAR_CLIENTES ? listarClientes() : cadastrarClientes()}
            <ul>
                {listClientes.map(listarClientes)}
            </ul>
        </div>
    );
};

export default App;
